import React, { Component } from 'react';
import './Convert.css';

export default class Convert extends Component {
  constructor(props) {
    super(props);

    this.state = {
      money_value: '',
      tomoney_value: 0
    };

    this.convertMethod = this.convertMethod.bind(this);
  }

  convertMethod() {
    let de_para = `${this.props.money}_${this.props.tomoney}`;
    let url = `http://free.currencyconverterapi.com/v5/convert?q=${de_para}&compact=y`;

    fetch(url)
      .then((res) => {
        return res.json();
      })
      .then((json) => {
        let cotacao = json[de_para];
        let tomoney_value = (
          parseFloat(this.state.tomoney_value) * cotacao
        ).toFixed(2);
        this.setState({ tomoney_value });
      });
  }

  render() {
    return (
      <div className='conversor'>
        <h2>
          {this.props.money} para {this.props.tomoney}
        </h2>
        <input
          type='text'
          onChange={(event) => {
            this.setState({ money_value: event.target.value });
          }}
        ></input>
        <button type='button' onClick={this.convertMethod()}>
          Converter
        </button>
        <h2>{this.state.tomoney_value}</h2>
      </div>
    );
  }
}
